    private static String stringifySize(long size, int order) {
        Locale locale = Locale.US;
        switch (order) {
            case 1:
                return String.format(locale, "%,13d", new Object[]{Long.valueOf(size)});
            case 1024:
                return String.format(locale, "%,9dK", new Object[]{Long.valueOf(size / OppoDeviceStorageMonitorService.KB_BYTES)});
            case 1048576:
                return String.format(locale, "%,5dM", new Object[]{Long.valueOf((size / OppoDeviceStorageMonitorService.KB_BYTES) / OppoDeviceStorageMonitorService.KB_BYTES)});
            case 1073741824:
                return String.format(locale, "%,1dG", new Object[]{Long.valueOf(((size / OppoDeviceStorageMonitorService.KB_BYTES) / OppoDeviceStorageMonitorService.KB_BYTES) / OppoDeviceStorageMonitorService.KB_BYTES)});
            default:
                throw new IllegalArgumentException("Invalid size order");
        }
    }

    private static String stringifyKBSize(long size) {
        return stringifySize(OppoDeviceStorageMonitorService.KB_BYTES * size, 1024);
    }

